import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import API_BASE_URL from "../config/api";
import QRCode from "qrcode.react";
import "../styles/Dashboard.css";
import { useNavigate } from "react-router-dom";
import NewSession from "./NewSession";
import SessionDetails from "./SessionDetails";
import SideNav from "../components/SideNav";

axios.defaults.withCredentials = true;

const TeacherDashboard = () => {
  //eslint-disable-next-line
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [sessionList, setSessionList] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [isSessionDisplay, setSessionDisplay] = useState(false);
  const [currentSession, setCurrentSession] = useState("");
  const [loading, setLoading] = useState(true);
  const [latestQR, setLatestQR] = useState("");
  const [latestSessionDetails, setLatestSessionDetails] = useState(null);
  const [showAllSessions, setShowAllSessions] = useState(false);
  const navigate = useNavigate();

  //update list of sessions
  const updateList = useCallback(async () => {
    try {
      setLoading(true);
      const response = await axios.post(
        `${API_BASE_URL}/sessions/getSessions`,
        {
          token: token,
        }
      );
      setSessionList(response.data.sessions || []);
    } catch (err) {
      console.error("Error fetching sessions:", err);
      if (err.response && err.response.status === 401) {
        // Token expired or invalid, redirect to login
        localStorage.clear();
        navigate("/login");
        return;
      }
      setSessionList([]);
    } finally {
      setLoading(false);
    }
  }, [token, navigate]);

  const toggleSessionDetails = (e) => {
    //get the session details that has session_id = e
    setCurrentSession(
      sessionList.filter((session) => {
        return session.session_id === e;
      })
    );
    setSessionDisplay(!isSessionDisplay);
  };

  const handleSessionCreated = (qrData, sessionDetails) => {
    // Refresh the session list and store QR data
    updateList();
    setLatestQR(qrData);
    setLatestSessionDetails(sessionDetails);
  };

  const togglePopup = () => {
    setIsOpen(!isOpen);
  };

  const copyLatestQR = () => {
    if (latestQR) {
      navigator.clipboard.writeText(latestQR);
      alert("QR code URL copied to clipboard!");
    }
  };

  // Check if a session is currently running
  const isSessionRunning = (session) => {
    const now = new Date();
    const sessionDate = new Date(session.date);
    const [hours, minutes] = session.time.split(':').map(Number);
    sessionDate.setHours(hours, minutes, 0, 0);
    
    // Parse duration (e.g., "2 hours" or "90 minutes")
    const durationMatch = session.duration.match(/(\d+)\s*(hour|minute)/i);
    if (!durationMatch) return false;
    
    const durationValue = parseInt(durationMatch[1]);
    const durationUnit = durationMatch[2].toLowerCase();
    const durationInMinutes = durationUnit.includes('hour') ? durationValue * 60 : durationValue;
    
    const sessionEndTime = new Date(sessionDate.getTime() + durationInMinutes * 60000);
    
    // Add 15-minute buffer after session ends
    const sessionEndWithBuffer = new Date(sessionEndTime.getTime() + 15 * 60000);
    
    return now >= sessionDate && now <= sessionEndWithBuffer;
  };

  // Stop/dismiss a running session
  const stopSession = async (sessionId) => {
    try {
      // You can implement this by either:
      // 1. Marking the session as ended in the database
      // 2. Or just removing it from the current view
      
      // For now, let's just refresh the session list
      await updateList();
      
      // Show success message
      alert("Session dismissed successfully!");
      
    } catch (err) {
      console.error("Error stopping session:", err);
      alert("Failed to stop session. Please try again.");
    }
  };

  useEffect(() => {
    if (token === "" || token === undefined) {
      navigate("/login");
    } else {
      const userType = localStorage.getItem("type");
      if (userType !== "teacher") {
        navigate("/student-dashboard");
        return;
      }
      updateList();
    }
  }, [token, navigate, updateList]);

  return (
    <>
      <div className="app-container">
        <SideNav onCreateSession={togglePopup} userType="teacher" />
        <div className="main-content-area">
          <div className="dashboard-main">
            <div className="section-header">
              <h1>👨‍🏫 Teacher Dashboard</h1>
              <div className="session-header-actions">
                <span className="session-count">{sessionList.length} sessions created</span>
                <button 
                  onClick={() => setShowAllSessions(!showAllSessions)}
                  className="view-all-btn"
                  style={{
                    backgroundColor: showAllSessions ? '#e74c3c' : '#3498db',
                    color: 'white',
                    border: 'none',
                    padding: '8px 16px',
                    borderRadius: '15px',
                    cursor: 'pointer',
                    fontSize: '14px',
                    marginLeft: '10px',
                    transition: 'all 0.3s ease'
                  }}
                >
                  {showAllSessions ? '📋 Show Recent' : '📂 View All Sessions'} →
                </button>
              </div>
            </div>
            
            {loading ? (
              <div className="loading-state">
                <div className="loading-icon">⏳</div>
                <p>Loading sessions...</p>
              </div>
            ) : (
              <div className="sessions-list">
                {sessionList.length > 0 ? (
                  <>
                    {(showAllSessions ? sessionList : sessionList.slice(0, 5)).map((session, index) => (
                      <div key={index} className="session-card">
                        <h3>{session.name}</h3>
                        <p>Date: {new Date(session.date).toLocaleDateString()}</p>
                        <p>Time: {session.time}</p>
                        <p>Duration: {session.duration}</p>
                        <p>Attendance: {session.attendance?.length || 0} students</p>
                        <button onClick={() => toggleSessionDetails(session.session_id)}>
                          View Details
                        </button>
                      </div>
                    ))}
                    {!showAllSessions && sessionList.length > 5 && (
                      <div className="view-all-container" style={{textAlign: 'center', marginTop: '20px'}}>
                        <p style={{color: '#7f8c8d', fontSize: '14px'}}>
                          Showing 5 of {sessionList.length} sessions
                        </p>
                      </div>
                    )}
                  </>
                ) : (
                  <div className="no-sessions">
                    <h3>No sessions created yet</h3>
                    <p>Click "Create Session" in the sidebar to get started</p>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
      
      {isSessionDisplay && (
        <div className="popup-overlay">
          <SessionDetails
            currentSession={currentSession}
            toggleSessionDetails={toggleSessionDetails}
          />
        </div>
      )}
      
      {isOpen && (
        <div className="popup-overlay">
          <NewSession togglePopup={togglePopup} />
        </div>
      )}
    </>
  );
};

export default TeacherDashboard;
