import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import API_BASE_URL from "../config/api";
import "../styles/Dashboard.css";
import { useNavigate } from "react-router-dom";
import NewSession from "./NewSession";
import SessionDetails from "./SessionDetails";
import SideNav from "../components/SideNav";

axios.defaults.withCredentials = true;

const TeacherDashboard = () => {
  //eslint-disable-next-line
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [sessionList, setSessionList] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [isSessionDisplay, setSessionDisplay] = useState(false);
  const [currentSession, setCurrentSession] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  //update list of sessions
  const updateList = useCallback(async () => {
    try {
      setLoading(true);
      const response = await axios.post(
        `${API_BASE_URL}/sessions/getSessions`,
        {
          token: token,
        }
      );
      setSessionList(response.data.sessions || []);
    } catch (err) {
      console.error("Error fetching sessions:", err);
      if (err.response && err.response.status === 401) {
        // Token expired or invalid, redirect to login
        localStorage.clear();
        navigate("/login");
        return;
      }
      setSessionList([]);
    } finally {
      setLoading(false);
    }
  }, [token, navigate]);

  const toggleSessionDetails = (e) => {
    //get the session details that has session_id = e
    setCurrentSession(
      sessionList.filter((session) => {
        return session.session_id === e;
      })
    );
    setSessionDisplay(!isSessionDisplay);
  };

  const togglePopup = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    if (token === "" || token === undefined) {
      navigate("/login");
    } else {
      // Check if user is actually a teacher
      const userType = localStorage.getItem("type");
      if (userType !== "teacher") {
        // If user is not a teacher, redirect to appropriate dashboard
        navigate("/student-dashboard");
        return;
      }
      updateList();
    }
  }, [token, navigate, updateList]);

  return (
    <>
      <div className="app-container">
        <SideNav onCreateSession={togglePopup} userType="teacher" />
        <div className="main-content-area">
          <div className="dashboard-main">
            <h1>Teacher Dashboard</h1>
            {loading ? (
              <div className="loading">Loading sessions...</div>
            ) : (
              <div className="session-list">
                {sessionList.length === 0 ? (
                  <div className="no-sessions">
                    <p>No sessions created yet.</p>
                    <button onClick={togglePopup} className="create-first-session-btn">
                      Create Your First Session
                    </button>
                  </div>
                ) : (
                  sessionList.map((session) => (
                    <div
                      className="session-card"
                      key={session.session_id}
                      onClick={() => toggleSessionDetails(session.session_id)}
                    >
                      <div className="session-info">
                        <h3>{session.name}</h3>
                        <p>Date: {session.date}</p>
                        <p>Time: {session.time}</p>
                        <p>Duration: {session.duration} minutes</p>
                      </div>
                      <div className="session-stats">
                        <span className="attendance-count">
                          {session.attendance ? session.attendance.length : 0} students attended
                        </span>
                      </div>
                    </div>
                  ))
                )}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Popup for creating new session */}
      {isOpen && (
        <div className="overlay">
          <div className="popup">
            <NewSession onClose={togglePopup} onSessionCreated={updateList} />
          </div>
        </div>
      )}

      {/* Popup for session details */}
      {isSessionDisplay && (
        <div className="overlay">
          <div className="popup">
            <SessionDetails
              onClose={() => setSessionDisplay(false)}
              currentSession={currentSession}
            />
          </div>
        </div>
      )}
    </>
  );
};

export default TeacherDashboard;
